import React, {useState} from "react";
import CommonSection from "../shared/CommonSection";

import { Container, Row, Col  } from "reactstrap";

import { useLocation } from "react-router-dom";
import LocationCard from "./../shared/LocationCard";
import useFetch from "../hooks/useFetch";
import { BASE_URL } from "../utils/config";

import GoogleMapComponent from "../components/GoogleMapComponent";

const SearchResultList = () => {
    const [pageCount, SetPageCount] = useState(0);
    const [page, setPage] = useState(0);
    const location = useLocation();
    const {data: locations, loading, error} = useFetch(`${BASE_URL}/locations?page=${page}`);
    const {data:locationCount} = useFetch(`${BASE_URL}/locations/search/getLocationcount`)

    useEffect(()=>{
        window.scrollTo(0,0)
        const pages = Math.ceil(locationCount/ 8)
        SetPageCount(pages)
    },[page, hotelCount])


    //console.log(data);


    return (
        <>
        <CommonSection title={"Search Result"}/>
        <section>
            <Container>
                <Row>
                    {data.length === 0 ? (
                        <h4 className="text-center">No location found </h4>
                    ) : (
                        data?.map(location => (
                            <Col lg="3" className="mb-4" key={location._id}>
                                <LocationCard location={location} />
                            </Col>
                        ))
                    )}
                </Row>
            </Container>
            
        </section>

        <section>
        <Container>
            <Row>
                <Col lg="12">
                    <GoogleMapComponent/>
                </Col>
            </Row>
        </Container>
    </section>
        </>
    )

};
export default SearchResultList;
