import React, {useState, useEffect} from "react";
import "../styles/hotels.css";
import LocationCard from "../shared/LocationCard";
import CommonSection from "../shared/hotel/CommonSection-hotel";
import SearchBar from "../shared/SearchBar";
import { Col, Container, Row } from "reactstrap";
import hotelData from "../assets/data/hotels";
const Hotels = () => {
    
    return <>
    <CommonSection title={"All Hotels"} />
    <section>
        <Container>
            <Row>
                <SearchBar />
            </Row>
        </Container>
    </section>
    <section>
        <Container>
            <Row>
                {
                    hotelData?.map(location => <Col lg="3" key={location.id}> <LocationCard location={location} /></Col>)

                }
            </Row>
        </Container>
    </section>
    </>

};
export default Hotels;
