import React, {useState, us} from "react";
import CommonSection from "../shared/CommonSection";

import { Container, Row, Col  } from "reactstrap";

import { useLocation } from "react-router-dom";
import LocationCard from "./../shared/LocationCard";
import useFetch from "../hooks/useFetch";
import { BASE_URL } from "../utils/config";

import GoogleMapComponent from "../components/GoogleMapComponent";

const SearchResultList = () => {
    const location = useLocation();
    const [data] = useState(location.state);

    const [pageCount, SetPageCount] = useState(0);
    const [page, setPage] = useState(0);
    const {data: locations, loading, error} = useFetch(`${BASE_URL}/locations?page=${page}`);

    const {data:locationCount} = useFetch(`${BASE_URL}/locations/search/getLocationcount`)

    useEffect(()=>{
        window.scrollTo(0,0)
        const pages = Math.ceil(locationCount/ 8)
        SetPageCount(pages)
    },[page, locationCount])


    //console.log(data);


    return (
        <>
        <CommonSection title={"Search Result"}/>
        <section>
            <Container>
            {loading && <h4 className="text-center pt-5">Loading...</h4>}
            {error && <h4 className="text-center pt-5">{error}</h4>}
            {!loading && !error &&
                <Row>
                    {data.length === 0 ? (
                        <h4 className="text-center">No location found </h4>
                    ) : (
                        data?.map(location => (
                            <Col lg="3" className="mb-4" key={location._id}>
                                <LocationCard location={location} />
                            </Col>
                        ))
                    )}
                    <Col lg="12">
                            <div className="pagination d-flex align-items-center
                            justify-content-center mt-4 gap-3">
                                {[...Array(pageCount).keys()].map(number => (
                                    <span key={number} onClick={() => setPage(number)}
                                    className={page===number ? "active__page" : ""}
                                    >
                                        {number + 1}
                                    </span>
                                ))}
                            </div>
                        </Col>
                </Row>
}
            </Container>
            
        </section>

        <section>
        <Container>
            <Row>
                <Col lg="12">
                    <GoogleMapComponent/>
                </Col>
            </Row>
        </Container>
    </section>
        </>
    )

};
export default SearchResultList;
