import React, {useState, useEffect} from "react";
import "../styles/restaurant.css";
import LocationCard from "../shared/LocationCard";
import SearchBar from "../shared/SearchBar";
import CommonSection from "../shared/restaurant/CommonSection-restaurant";
import restaurantData from "../assets/data/restaurant";
import { Container, Row, Col } from "reactstrap";



const Restaurants = () => {

    const [pageCount, SetPageCount] = useState(0)
    const [page, setPage] = useState(0)

    useEffect(()=>{
        const pages = Math.ceil(5/ 4)
        SetPageCount(pages)
    },[page])



    return (
    <>
        <CommonSection title={"All Restaurants"}/>
        <section>
            <Container>
                <Row>
                    <SearchBar />
                </Row>
            </Container>
        </section>
        <section className="pt-0">
            <Container>
                <Row>
                    {
                        restaurantData?.map(location => (
                        <Col lg="3"  className="mb-4" key={location.id}>
                             <LocationCard  location={location} />
                        </Col>
                        ))}
                        <Col lg="12">
                            <div className="pagination d-flex align-items-center
                            justify-content-center mt-4 gap-3">
                                {[...Array(pageCount).keys()].map(number => (
                                    <span key={number} onClick={() => setPage(number)}
                                    className={page===number}
                                    >
                                        {number + 1}
                                    </span>
                                ))}
                            </div>
                        </Col>
                </Row>
            </Container>
        </section>
    </>
    )

};
export default Restaurants;
