import React from "react";
import '../styles/restaurant-details.css';
import {Container, Row, Col, Form, ListGroup } from 'reactstrap';
import {useParams} from 'react-router-dom';
import restaurantData from '../assets/data/restaurant';
import useFetch from "../hooks/useFetch";
import { BASE_URL } from "../utils/config";



const RestaurantsDetails = () => {

    const {id} = useParams()
    //fetch data 

    const {data:restaurant} = useFetch(`${BASE_URL}/restaurants/${id}`);

    const {name, city, photo, avgRating, email, contact, desc, address} = restaurant;

    useEffect(()=>{
        window.scrollTo(0,0)
    },[])


    return <>
     
     <section>
        <Container>
            {!loading && !error &&<Row>
                <Col lg="8">
                <div className="restaurant__info" >
                            <h2>{name}</h2>
                    <div className="restaurant__content">
                        <img src={photo} alt="" />
                        <div className="d-flex align-items-center gap-5">
                            <span className="entreprise__restaurant d-flex align-items-center gap-1">
                            <i class="ri-map-pin-fill" style={{color: "var(--secondary-color)" }}></i> {city}
                            </span>
                            <span className="entreprise___rating d-flex align-items-center gap-1">
                                <i class="ri-star-fill" style={{color: "var(--secondary-color)" }}></i> {avgRating}
                            </span>

                        </div>
                    </div>
                    <div className="restaurant__extra-details">
                    <h6>{address}</h6>
                    <span>
                        <i class="ri-phone-fill" style={{color: "var(--secondary-color)" }}></i> {contact}
                    </span>
                    <span>
                    <i class="ri-mail-fill" style={{color: "var(--secondary-color)" }}></i> {email}
                    </span>
                    <h6>Description</h6>
                    <p>{desc}</p>
                    </div>
                </div>
                
                </Col>
            </Row>
}
        </Container>
     </section>


    </>

};
export default RestaurantsDetails;
