import React, {useState, useEffect} from "react";
import "../styles/restaurant.css";
import RestaurantCard from "../shared/RestaurantCard";
import SearchBar from "../shared/SearchBar1";
import CommonSection from "../shared/restaurant/CommonSection-restaurant";
import restaurantData from "../assets/data/restaurant";
import { Container, Row, Col } from "reactstrap";

import useFetch from "../hooks/useFetch";
import { BASE_URL } from "../utils/config";



const Restaurants = () => {
    const {data: restaurant, loading, error} = useFetch(`${BASE_URL}/restaurants/`);
    console.log(restaurant);

    const [pageCount, SetPageCount] = useState(0)
    const [page, setPage] = useState(0)

    useEffect(()=>{
        const pages = Math.ceil(5/ 4)
        SetPageCount(pages)
    },[page])



    return (
    <>
        <CommonSection title={"All Restaurants"}/>
        <section>
            <Container>
                <Row>
                    <SearchBar />
                </Row>
            </Container>
        </section>
        <section className="pt-0">
            <Container>
                <Row>
                {
                loading && <h4>Loading...</h4>
                }
                {
                error && <h4>{error}</h4>
                }
                    {
                        !lorestaurant?.map(restaurant => (
                        <Col lg="3"  className="mb-4" key={restaurant._id}>
                             <RestaurantCard  restaurant={restaurant} />
                        </Col>
                        ))}
                        <Col lg="12">
                            <div className="pagination d-flex align-items-center
                            justify-content-center mt-4 gap-3">
                                {[...Array(pageCount).keys()].map(number => (
                                    <span key={number} onClick={() => setPage(number)}
                                    className={page===number ? "active__page" : ""}
                                    >
                                        {number + 1}
                                    </span>
                                ))}
                            </div>
                        </Col>
                </Row>
            </Container>
        </section>
    </>
    )

};
export default Restaurants;
