import React, { useEffect, useState } from 'react';

const GoogleMapComponent = () => {
  const [map, setMap] = useState(null);
  const [directionsService, setDirectionsService] = useState(null);
  const [directionsRenderer, setDirectionsRenderer] = useState(null);
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [distance, setDistance] = useState('');
  const [duration, setDuration] = useState('');

  useEffect(() => {
    const googleMapsScript = document.createElement('script');
    googleMapsScript.src = `https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY&libraries=places`;
    window.document.body.appendChild(googleMapsScript);

    googleMapsScript.addEventListener('load', () => {
      const googleMaps = window.google.maps;
      const mapInstance = new googleMaps.Map(document.getElementById('map'), {
        center: { lat: 37.7749, lng: -122.4194 },
        zoom: 8
      });
      setMap(mapInstance);
      setDirectionsService(new googleMaps.DirectionsService());
      setDirectionsRenderer(new googleMaps.DirectionsRenderer());
    });

    return () => {
      const googleMapsScript = document.querySelector('script[src^="https://maps.googleapis.com"]');
      if (googleMapsScript) {
        window.document.body.removeChild(googleMapsScript);
      }
    }
  }, []);

  const handleSearch = () => {
    const googleMaps = window.google.maps;
    const geocoder = new googleMaps.Geocoder();
    geocoder.geocode({ address: origin }, (results, status) => {
      if (status === 'OK') {
        const originLocation = results[0].geometry.location;
        geocoder.geocode({ address: destination }, (results, status) => {
          if (status === 'OK') {
            const destinationLocation = results[0].geometry.location;
            directionsService.route({
              origin: originLocation,
              destination: destinationLocation,
              travelMode: googleMaps.TravelMode.DRIVING
            }, (response, status) => {
              if (status === 'OK') {
                directionsRenderer.setDirections(response);
                directionsRenderer.setMap(map);

                const route = response.routes[0];
                let totalDistance = 0;
                let totalDuration = 0;
                for (let i = 0; i < route.legs.length; i++) {
                  totalDistance += route.legs[i].distance.value;
                  totalDuration += route.legs[i].duration.value;
                }

                const distanceInKm = (totalDistance / 1000).toFixed(1);
                setDistance(distanceInKm);
                const durationInMinutes = Math.round(totalDuration / 60);
                setDuration(durationInMinutes);

                const bounds = new googleMaps.LatLngBounds();
                route.overview_path.forEach((path) => bounds.extend(path));
                map.fitBounds(bounds);
              } else {
                console.log(`Directions request failed: ${status}`);
              }
            });
          } else {
            console.log(`Geocode was not successful for the following reason: ${status}`);
          }
        });
      } else {
        console.log(`Geocode was not successful for the following reason: ${status}`);
      }
    });
  };

  return (
    <>
      <div>
        <input type="text" placeholder="Enter origin address" value={origin} onChange={(e) => setOrigin(e.target.value)} />
        <input type="text" placeholder="Enter destination address" value={destination} onChange={(e) => setDestination(e.target.value)} />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div id="map" style={{ height: "500px" }}></div>
    </>
  );
};

export default GoogleMapComponent;
