import React from "react";
import '../styles/entreprise-details.css';
import {Container, Row, Col, Form, ListGroup } from 'reactstrap';
import {useParams} from 'react-router-dom';
import locationData from '../assets/data/locations';



const EntreprisesDetails = () => {

    const {id} = useParams()

    const location = locationData.find(location => location.id === id)

    const {name, city, photo, avgRating, email, contact, desc, address} = location

    return <>
     
     <section>
        <Container>
            <Row>
                <Col lg="8">
                <div className="location__info" >
                            <h2>{name}</h2>
                    <div className="location__content">
                        <img src={photo} alt="" />
                        <div className="d-flex align-items-center gap-5">
                            <span className="entreprise__location d-flex align-items-center gap-1">
                                <i class="ri-map-pin-line"></i> {city}
                            </span>
                            <span className="entreprise__rating d-flex align-items-center gap-1">
                                <i class="ri-star-fill" style={{color: "var(--secondary-color)" }}></i> {avgRating}
                            </span>
                        <div className="d-flex align-items-center gap-5">
                            <h6>{address}</h6>
                        </div>

                        </div>
                    </div>
                </div>
                </Col>
            </Row>
        </Container>
     </section>


    </>

};
export default EntreprisesDetails;
