import React, {useState, useEffect} from "react";
import "../styles/hotels.css";
import HotelCard from "../shared/HotelCard";
import CommonSection from "../shared/hotel/CommonSection-hotel";
import SearchBar from "../shared/SearchBar1";
import { Col, Container, Row } from "reactstrap";
import hotelData from "../assets/data/hotels";

const Hotels = () => {
    const {data: restaurant, loading, error} = useFetch(`${BASE_URL}/restaurants/`);
    console.log(restaurant);

    const [pageCount, SetPageCount] = useState(0)
    const [page, setPage] = useState(0)

    useEffect(()=>{
        const pages = Math.ceil(5/ 4)
        SetPageCount(pages)
    },[page])


    return (
    <>
    <CommonSection title={"All Hotels"} />
    <section>
        <Container>
            <Row>
                <SearchBar />
            </Row>
        </Container>
    </section>
    <section>
        <Container>
            <Row>
                {
                    hotelData?.map(hotel => (
                    <Col lg="3" key={hotel.id}>
                         <HotelCard hotel={hotel} />
                         </Col>
                    ))}
                    <Col lg="12">
                            <div className="pagination d-flex align-items-center
                            justify-content-center mt-4 gap-3">
                                {[...Array(pageCount).keys()].map(number => (
                                    <span key={number} onClick={() => setPage(number)}
                                    className={page===number ? "active__page" : ""}
                                    >
                                        {number + 1}
                                    </span>
                                ))}
                            </div>
                        </Col>

            </Row>
        </Container>
    </section>
    </>
    )

};
export default Hotels;
